mapper.select:
SELECT a.id, a.pid, c.resource_name AS pname, a.`resource_name` AS resourceName, a.`resource_url` AS resourceUrl, a.`resource_type` AS resourceType, a.`icon`, b.`dic_value` AS resourceTypeName, a.`description`, a.`order_no` AS orderNo FROM sys_resource a LEFT JOIN sys_direction b ON b.dic_key = a.resource_type LEFT JOIN sys_resource c ON c.id = a.pid WHERE b.dic_code = #{typeCode} AND a.state = #{state}
AND a.resource_name LIKE #{resourceNamePattern}
ORDER BY a.order_no ASC

SELECT a.id, a.pid, a.resource_name AS resourceName, b.roleId FROM  sys_resource  a
LEFT  JOIN (
    SELECT  role_id AS roleId, resource_id
    FROM  sys_role_relation_resource
    WHERE role_id = #{roleId}
) b
ON b.resource_id = a.id
WHERE   a.state = #{state}
ORDER BY a.order_no ASC
